create table if not exists PLANTS_IN_GS_GROW_SPACE (PLANTS_IN_GS_ID integer not null, GROW_SPACE_ID integer not null, primary key (PLANTS_IN_GS_ID, GROW_SPACE_ID)) engine=MyISAM
