INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Goldmarie','Bidens ferulifolia','Goldmarie verwandelt den Balkon in ein gelbes Blütenmeer. Sie ist eine prächtige Hängepflanze und ein wertvoller Bienenmagnet für den Balkon. Natürlicherweise kommt die Goldmarie in vielen heißen bis gemäßigten Regionen der Erde vor.','Zierpflanze','dicht, buschig','Mai bis Oktober','gelb','ungiftig','Ja',NULL,'Blütenschmuck',NULL,'sonnig','sandig bis lehmig','frisch bis feucht',NULL,'reich','Die Goldmarie hat einen hohen Wasserbedarf und muss vor allem im Sommer reichlich gegossen werden. Grundsätzlich gilt: Gießen Sie die Balkonblume lieber langsam und regelmäßig und achten Sie darauf, dass die Erde das Wasser komplett aufnimmt. Die ideale Gießzeit ist frühmorgens oder abends.','https://www.mein-schoener-garten.de/pflanzen/goldmarie-zweizahn/bidens-zweizahn-goldmarie');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Blaues Gänseblümchen','Brachyscome iberidifolia','Das Blaue Gänseblümchen ist ein echter Dauerblüher. Den Namen verdankt die Pflanze lediglich ihrer optischen Ähnlichkeit mit unserem heimischen Gänseblümchen, botanisch betrachtet haben die beiden Arten nur wenig Gemeinsamkeiten. Die einjährige Pflanze ist erst seit den 1980er Jahren als Zuchtform im Handel erhältlich.','Zierpflanze','kugelförmig, buschig','Mai bis Oktober','violett, blau','ungiftig',NULL,NULL,'Blütenschmuck, Duftpflanze',NULL,'sonnig','sandig bis lehmig','frisch','neutral','reich','Verblühtes sollte regelmäßig entfernt werden, um sich den ganzen Sommer über an der Blütenpracht erfreuen zu können. Ein Rückschnitt ist nicht nötig, da die Pflanze einjährig kultiviert wird. Das Blaue Gänseblümchen ist nicht frosthart, Temperaturen unter sechs Grad Celsius hält es nicht aus.','https://www.mein-schoener-garten.de/pflanzen/blaues-gaensebluemchen/blaues-gaensebluemchen');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Eisbegonien','Begonia semperflorens','Eisbegonien halten zwar keine Kälte aus, in der warmen Jahreszeit sind sie dafür umso schöner. Die kleinen Blühwunder haben ihren Ursprung in Brasilien. In ihrer brasilianischen Heimat blühen die Pflanzen mehrere Jahre durchgängig ohne Unterlass. Der deutsche Name "Eisbegonie" ist dagegen irreführend. Er deutet nämlich keineswegs auf ihre besondere Frosthärte hin, sondern beschreibt lediglich die leicht brechenden Laubblätter.','Zierpflanze','aufrecht, buschig','Mai bis Oktober','rosa, weiß','ungiftig',NULL,NULL,'Blütenschmuck',NULL,'absonnig bis halbschattig','sandig bis lehmig','trocken bis frisch','neutral','mäßig','Beim Gießen der Pflanzen sollten Sie vor allem im Topf behutsam vorgehen. Eisbegonien mögen es zwar feucht, doch sollte sich im Substrat keine Nässe stauen. Zum Überwintern im Haus graben Sie die Pflanze vor dem ersten Nachtfrost aus und setzen sie in einen Topf mit feuchter Anzuchterde.','https://www.mein-schoener-garten.de/pflanzen/begonie/eisbegonie-semperflorens-begonie');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Bärlauch','Allium ursinum','Bärlauch ist eine beliebte ausdauernde und heilkräftige Würzpflanze. Am idealen Standort gedeiht das Wildgemüse nicht nur im Wald, sondern auch im Garten. Bärlauch gehört zur Familie der Lauchgewächse und ist mit Gemüse wie Knoblauch und Zwiebeln verwandt. Unverkennbar ist das am Duft, den die Pflanzen versprühen.','Nutzpflanze','überhängend, teppichbildend','April bis Juni','weiß','ungiftig',NULL,'Ja','Blütenschmuck, Duftpflanze','Küchenkraut, Heilpflanze','absonnig bis schattig','sandig bis lehmig','frisch bis feucht','alkalisch bis neutral','reich','Eine besondere Pflege des Bärlauchs ist nicht nötig, sofern er sich an seinem Standort wohlfühlt. Wichtig ist, dass der Boden während der Saison ausreichend feucht ist. Daher sollte man die Pflanzen in Trockenperioden auch etwas wässern.','https://www.mein-schoener-garten.de/pflanzen/lauch/baerlauch');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Haselnussstrauch','Corylus avellana','Der heimische Haselnussstrauch bereichert jeden größeren naturnahen Garten. Es gibt aber auch Sorten, die mit einem kompakteren Wuchs und einer Menge Haselnüsse selbst urbanen Gärten gut zu Gesicht stehen.','Nutzpflanze','ausladend, buschig','Februar bis März','gelb, rot','ungiftig','Ja','Ja',NULL,'Vogelschutz, Insektenschutz, Wildobst','sonnig bis halbschattig','sandig bis lehmig','frisch','alkalisch bis neutral','mäßig bis reich','Weniger ist mehr: Eine Portion Kompost im Frühjahr reicht aus, um den Haselnussstrauch ausreichend mit Nährstoffen zu versorgen. Während die Früchte reifen, sollte man den Strauch bei anhaltend trockenem Wetter gut wässern, damit er sie nicht vorzeitig abwirft. Ansonsten braucht er keine besondere Aufmerksamkeit.','https://www.mein-schoener-garten.de/pflanzen/haselnuss/haselnussstrauch-gewoehnliche-haselnuss');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Gemeiner Wacholder','Juniperus communis','Der Wacholder ist ein heimisches Wildgehölz und gehört zur Familie der Zypressengewächse. Es gibt von ihm zahlreiche Gartenformen – vom flach wachsenden Kriechwacholder bis zum schmal-aufrechten Säulen-Wacholder. Sie wachsen vorwiegend auf mageren, mäßig trockenen Böden – eine ökologische Nische, in der die meisten anderen heimischen Bäume und Sträucher nicht lebensfähig sind.','Zier- und Nutzpflanze','säulenförmig, aufrecht','April bis Juni',NULL,'ungiftig',NULL,'Ja','Fruchtschmuck, malerischer Wuchs','Gewürz','sonnig bis absonnig','sandig bis lehmig','trocken','neutral','arm','Wacholder kommt ohne besondere Pflegemaßnahmen aus.  Sollten die Pflanzen trotz ausreichend Sonnenlicht braune Triebspitzen zeigen, leiden sie in den meisten Fällen unter Magnesiummangel. Abhilfe schafft eine Gabe Bittersalz.','https://www.mein-schoener-garten.de/pflanzen/wacholder/gemeiner-wacholder');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Ananasminze','Mentha suaveolens ‘Variegata’','Die Ananasminze besticht nicht nur durch fruchtiges Aroma und wohltuende Frische, sondern auch durch ihre panaschierten Blätter. Sie zählt zu den Frucht-Minzen, die weniger Menthol enthalten als klassische Minzen und dafür mehr nach frischen Früchten duften und schmecken. Weil das fruchtige Aroma der Ananas überwiegt, ist die Ananasminze auch bei Kindern beliebt.','Nutzpflanze','aufrecht','Juli bis September','rosa, weiß','ungiftig','Ja',NULL,'Duftpflanze','Küchenkraut, Heilpflanze','sonnig bis halbschattig','sandig bis lehmig','frisch bis feucht','neutral','mäßig bis reich','Ananasminze ist anspruchslos und pflegeleicht. Die Erde sollte man gleichmäßig feucht halten und nicht austrocknen lassen. Vor allem Kübelpflanzen brauchen im Sommer regelmäßig Wasser und reagieren empfindlich auf Trockenheit. Gelegentliche Staunässe hingegen verzeihen sie.','https://www.mein-schoener-garten.de/pflanzen/minze-pfefferminze/ananasminze');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Echte Kamille','Matricaria chamomilla','Die Kamille wird seit Jahrhunderten für ihre beruhigende und schmerzlindernde Wirkung geschätzt. Im Spätmittelalter und in Kräuterbüchern von Klöstern wird stets die entzündungshemmende und heilende Wirkung der Pflanze beschrieben, die durchweg den Ruf als "gutes Kraut" genießt.','Zier- und Nutzpflanze','locker, buschig','Mai bis Juli','weiß, gelb','ungiftig',NULL,'Ja','Blütenschmuck','Heilpflanze','sonnig','sandig bis lehmig','trocken bis frisch','alkalisch bis neutral','reich','Einmal angewachsen ist die Kamille äußerst anspruchslos. Nach der Blüte können Sie die Pflanzen gegebenenfalls zurückschneiden, um neue Triebe zu fördern.','https://www.mein-schoener-garten.de/pflanzen/kamille/echte-kamille');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Koriander','Coriandrum sativum','Koriander mag man – oder man mag ihn nicht. Sein kräftiges Aroma wird vor allem in der asiatischen Küche geschätzt. Er gibt aber auch heimischen Gerichten die spezielle würzige Note. Ursprünglich stammt das Kraut aber aus dem Mittelmeerraum und dem Mittleren Osten, wo es schon vor 3.000 Jahren verwendet wurde.','Nutzpflanze','locker, buschig','Juni bis Juli','weiß','ungiftig','Ja',NULL,'Blattschmuck','Küchenkraut','sonnig bis schattig','sandig bis lehmig','trocken bis feucht','neutral','reich','In der Anzuchtphase ist regelmäßiges Gießen wichtig, um vorzeitiges Schossen zu verhindern. Ein aufgelockerter Boden fördert das Wachstum des Korianders.','https://www.mein-schoener-garten.de/pflanzen/koriander/koriander');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Petersilie','Petroselinum crispum','Petersilie zählt zusammen mit Schnittlauch zu den Küchenkräutern, die man unbedingt im Garten haben sollte. Mit der richtigen Fruchtfolge können fast das ganze Jahr hindurch feine aromatische Blätter geerntet werden. Petersilie enthält viele Mineralstoffe wie Eisen und Calcium, ätherische Öle sowie einen hohen Anteil an den Vitaminen A, B und C.','Nutzpflanze','aufrecht, dicht','Juni bis Juli','gelb','ungiftig',NULL,NULL,NULL,'Küchenkraut','sonnig bis halbschattig','sandig bis lehmig','frisch bis feucht','alkalisch bis neutral','mäßig','Petersilie braucht viel Feuchtigkeit, verträgt jedoch keine Staunässe. Das regelmäßige Lockern des Bodens mit der Hacke fördert einen erneuten Austrieb nach der ersten Ernte. Wie alle Doldenblütler darf Petersilie nur alle vier bis fünf Jahre auf demselben Beet angebaut werden. Auch andere Doldenblütler wie zum Beispiel Möhren sollten in dieser Zeit nicht auf dem Beet stehen.','https://www.mein-schoener-garten.de/pflanzen/petersilie/petersilie');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Schnittlauch','Allium schoenoprasum','Schnittlauch gehört neben der Petersilie zu den beliebtesten Küchenkräutern und schmeckt am besten frisch aus dem Garten – und mit seinen rosa Kugelblüten ist er auch ein toller Beetschmuck.','Nutzpflanze','aufrecht','Mai bis August','violett, rot','ungiftig','Ja','Ja','Blütenschmuck','Küchenkraut','sonnig','sandig bis lehmig','trocken bis feucht','alkalisch bis neutral','reich','Schnittlauch ist im Grunde sehr pflegeleicht. Er braucht allerdings im Sommer eine gute Wasserversorgung, sonst werden die Röhrenblätter trocken und strohig.','https://www.mein-schoener-garten.de/pflanzen/lauch/schnittlauch');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Avocadobaum','Persea americana','Der Avocadobaum ist robuster als man denkt und kann auch bei uns – im Kübel kultiviert – leckere Früchte tragen. Seit dem 18. Jahrhundert verbreitete sich der Avocadobaum weltweit in den Tropen und Subtropen und ist inzwischen eine der wirtschaftlich bedeutendsten Kulturpflanzen.','Zier- und Nutzpflanze','aufrecht','April bis Mai','grün, gelb',NULL,NULL,NULL,'Blütenschmuck, Blattschmuck','Obst','sonnig','sandig bis lehmig','frisch','neutral','reich','Je wärmer die Pflanze steht, umso mehr Wasser braucht sie. Allerdings verdunstet der Avocadobaum durch das ledrige Laub weniger Wasser als Pflanzen mit weichen Blättern. Gießen Sie also regelmäßig, aber mit Bedacht. Sowohl Staunässe oder als auch komplette Bodentrockenheit gilt es zu verhindern. Trockenschäden erkennt man an braunen Blatträndern.','https://www.mein-schoener-garten.de/pflanzen/avocado-isabellenholz/avocadobaum');
