INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Hornveilchen','Viola cornuta','Hornveilchen gehören zur Gattung der Veilchen und sind kleiner als die ähnlich aussehenden Stiefmütterchen. Die winterharten Hornveilchen sorgen im Frühjahr und Herbst für bunten Blütenschmuck auf Terrasse und Balkon. An den verzweigten Stielen erscheinen fast die ganze Saison hindurch immer wieder neue Blüten.','Zierpflanze','buschig','März bis Oktober','mehrfarbig','ungiftig',NULL,NULL,'Blütenschmuck',NULL,'sonnig bis halbschattig','sandig bis lehmig','frisch','neutral bis sauer','mäßig','Gepflanzt werden die jungen Hornveilchen von März an bis in den Oktober hinein. Die meisten Hornveilchen sind bis -15 Grad winterhart und setzen oft nur bei Frost mit ihrer Blüte aus. Man sollte sie dann mit Tannenreisig oder Vlies abdecken, um sie vor Kahlfrost und Austrocknung durch die Wintersonne zu schützen.','https://www.mein-schoener-garten.de/pflanzen/veilchen-stiefmuetterchen/hornveilchen');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Goldmarie','Bidens ferulifolia','Goldmarie verwandelt den Balkon in ein gelbes Blütenmeer. Sie ist eine prächtige Hängepflanze und ein wertvoller Bienenmagnet für den Balkon. Natürlicherweise kommt die Goldmarie in vielen heißen bis gemäßigten Regionen der Erde vor.','Zierpflanze','dicht, buschig','Mai bis Oktober','gelb','ungiftig','Ja',NULL,'Blütenschmuck',NULL,'sonnig','sandig bis lehmig','frisch bis feucht',NULL,'reich','Die Goldmarie hat einen hohen Wasserbedarf und muss vor allem im Sommer reichlich gegossen werden. Grundsätzlich gilt: Gießen Sie die Balkonblume lieber langsam und regelmäßig und achten Sie darauf, dass die Erde das Wasser komplett aufnimmt. Die ideale Gießzeit ist frühmorgens oder abends.','https://www.mein-schoener-garten.de/pflanzen/goldmarie-zweizahn/bidens-zweizahn-goldmarie');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Blaues Gänseblümchen','Brachyscome iberidifolia','Das Blaue Gänseblümchen ist ein echter Dauerblüher. Den Namen verdankt die Pflanze lediglich ihrer optischen Ähnlichkeit mit unserem heimischen Gänseblümchen, botanisch betrachtet haben die beiden Arten nur wenig Gemeinsamkeiten. Die einjährige Pflanze ist erst seit den 1980er Jahren als Zuchtform im Handel erhältlich.','Zierpflanze','kugelförmig, buschig','Mai bis Oktober','violett, blau','ungiftig',NULL,NULL,'Blütenschmuck, Duftpflanze',NULL,'sonnig','sandig bis lehmig','frisch','neutral','reich','Verblühtes sollte regelmäßig entfernt werden, um sich den ganzen Sommer über an der Blütenpracht erfreuen zu können. Ein Rückschnitt ist nicht nötig, da die Pflanze einjährig kultiviert wird. Das Blaue Gänseblümchen ist nicht frosthart, Temperaturen unter sechs Grad Celsius hält es nicht aus.','https://www.mein-schoener-garten.de/pflanzen/blaues-gaensebluemchen/blaues-gaensebluemchen');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Eisbegonien','Begonia semperflorens','Eisbegonien halten zwar keine Kälte aus, in der warmen Jahreszeit sind sie dafür umso schöner. Die kleinen Blühwunder haben ihren Ursprung in Brasilien. In ihrer brasilianischen Heimat blühen die Pflanzen mehrere Jahre durchgängig ohne Unterlass. Der deutsche Name "Eisbegonie" ist dagegen irreführend. Er deutet nämlich keineswegs auf ihre besondere Frosthärte hin, sondern beschreibt lediglich die leicht brechenden Laubblätter.','Zierpflanze','aufrecht, buschig','Mai bis Oktober','rosa, weiß','ungiftig',NULL,NULL,'Blütenschmuck',NULL,'absonnig bis halbschattig','sandig bis lehmig','trocken bis frisch','neutral','mäßig','Beim Gießen der Pflanzen sollten Sie vor allem im Topf behutsam vorgehen. Eisbegonien mögen es zwar feucht, doch sollte sich im Substrat keine Nässe stauen. Zum Überwintern im Haus graben Sie die Pflanze vor dem ersten Nachtfrost aus und setzen sie in einen Topf mit feuchter Anzuchterde.','https://www.mein-schoener-garten.de/pflanzen/begonie/eisbegonie-semperflorens-begonie');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Große Kapuzinerkresse','Tropaeolum majus','Mit ihren großen Blüten in den verschiedenen Gelb-, Orange-, Rosarot- und Rot-Tönen setzt die Große Kapuzinerkresse im Zier- und Gemüsegarten ein leuchtendes Zeichen. Ihre Ranken können in einer Saison bis zu drei Meter lang werden. Die Blüten der Großen Kapuzinerkresse haben auch ein senfähnliches Aroma, sind aber etwas milder, wodurch sie eine beliebte Salatdekoration sind.','Zier- und Nutzpflanze','flachwüchsig','Juli bis Oktober','gelb, orange, rot','ungiftig',NULL,NULL,'Blütenschmuck','Heilpflanze, verzehrbar','sonnig bis halbschattig','sandig bis lehmig','trocken',NULL,'mäßig','Die Große Kapuzinerkresse ist pflegeleicht und wächst am liebsten ungestört. Gibt man der Kapuzinerkresse eine Rankhilfe zur Seite, klettert sie in die Höhe, ansonsten wächst sie kriechend.','https://www.mein-schoener-garten.de/pflanzen/kapuzinerkresse/grosse-kapuzinerkresse');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Verbene','Verbena','Blütenreich, pflegeleicht und anspruchslos. Bereits in der Antike wurden dieser in Europa heimische Pflanze wundertätige und beschützende Eigenschaften zugeschrieben. Die heute für Kasten- und Kübelbepflanzungen erhältlichen Verbenen sind allesamt Hybriden mit langer Züchtungs- und Kreuzungsgeschichte.','Zierpflanze','überhängend','Mai bis Oktober','mehrfarbig',NULL,'Ja',NULL,'Blütenschmuck',NULL,'sonnig bis halbschattig','sandig bis lehmig','frisch','neutral','reich','Verbenen sind nicht besonders anspruchsvoll. Die Sommerblüher können fast die ganze Saison über gesetzt werden, sind aber extrem frostempfindlich. Damit Verbenen prächtig gedeihen, sind eigentlich nur regelmäßige Wasser- und Düngergaben vonnöten. An einem heißen Sommertag können das bis zu 1,5 Liter Wasser pro Pflanze sein.','https://www.mein-schoener-garten.de/pflanzen/eisenkraut-verbene/verbene-eisenkraut');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Enzianstrauch','Lycianthes rantonnetii','Der von Mai bis Oktober blühende Enzianstrauch bereichert jeden Sitzplatz. Seine blauen oder weißen sternförmigen Blüten werden in der Mitte von leuchtend gelben Staubblättern geschmückt. Die pflegeleichte Kübelpflanze ist auch als Blauer Kartoffelstrauch bekannt.','Zierpflanze','aufrecht, überhängend','Mai bis Oktober','violett, blau','schwach giftig',NULL,NULL,'Blütenschmuck, malerischer Wuchs',NULL,'sonnig',NULL,'frisch bis feucht',NULL,'reich','Damit Enziansträucher viele Blüten bilden, brauchen sie im Sommer reichlich Wasser. An heißen Tagen müssen Sie eventuell morgens und abends zur Gießkanne greifen. Verlieren die Pflanzen im unteren Bereich Blätter, ist dies meist ein Zeichen für Nährstoff- und Wassermangel.','https://www.mein-schoener-garten.de/pflanzen/enzianstrauch/enzianstrauch');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Gewöhnlicher Liguster','Ligustrum vulgare','Liguster gehört zu den pflegeleichtesten heimischen Ziergehölzen, ist die perfekte Heckenpflanze und wichtiges Vogel- und Insektenschutzgehölz. Mit ihrem intensiven Duft locken die Blüten des Ligusters Bienen, Schmetterlinge und Fliegen an.','Zierpflanze','ausladend, aufrecht','Juni bis Juli','weiß','schwach giftig','Ja','Ja','Blütenschmuck, Fruchtschmuck','Vogelschutz, Insektenschutz','sonnig bis schattig','sandig bis tonig','feucht bis trocken','alkalisch bis neutral','mäßig','Der Gemeine Liguster verträgt Trockenheit ohne Probleme und ist daher auch im Sommer äußerst pflegeleicht. Eine Portion Kompost im Frühjahr reicht dem Strauch als Dünger vollkommen aus.','https://www.mein-schoener-garten.de/pflanzen/liguster-rainweide/gewoehnlicher-liguster');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Goldregen','Laburnum x watereri','Der Goldregen gehört zur Familie der Schmetterlingsblütler. Das Holz des Goldregens ist aufgrund seiner besonderen Farbgebung als Schnittholz sehr beliebt. Die goldgelben Schmetterlingsblüten sitzen dicht an dicht und verströmen einen starken Duft. Sie öffnen sich von Mai bis Juni und werden intensiv von Bienen und anderen Insekten beflogen.','Zierpflanze','aufrecht','Mai bis Juni','gelb','stark giftig','Ja',NULL,'Blütenschmuck, malerischer Wuchs, Duftpflanze',NULL,'sonnig bis halbschattig','sandig bis lehmig','trocken bis frisch','alkalisch bis neutral','mäßig','Der Goldregen besitzt ein fleischiges, wenig verzweigtes und eher flaches Wurzelwerk. Er gehört zu den Gehölzen, die sich schon nach ein paar Jahren nicht mehr gut verpflanzen lassen – daher sollte der im Garten gewählte Platz möglichst der endgültige sein. Besondere Pflegemaßnahmen oder gar Dünger braucht der Goldregen nicht – den für das Wachstum benötigten, mineralisch gebundenen Stickstoff erhält er aus der Luft.','https://www.mein-schoener-garten.de/pflanzen/goldregen/goldregen-vossii');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Haselnussstrauch','Corylus avellana','Der heimische Haselnussstrauch bereichert jeden größeren naturnahen Garten. Es gibt aber auch Sorten, die mit einem kompakteren Wuchs und einer Menge Haselnüsse selbst urbanen Gärten gut zu Gesicht stehen.','Nutzpflanze','ausladend, buschig','Februar bis März','gelb, rot','ungiftig','Ja','Ja',NULL,'Vogelschutz, Insektenschutz, Wildobst','sonnig bis halbschattig','sandig bis lehmig','frisch','alkalisch bis neutral','mäßig bis reich','Weniger ist mehr: Eine Portion Kompost im Frühjahr reicht aus, um den Haselnussstrauch ausreichend mit Nährstoffen zu versorgen. Während die Früchte reifen, sollte man den Strauch bei anhaltend trockenem Wetter gut wässern, damit er sie nicht vorzeitig abwirft. Ansonsten braucht er keine besondere Aufmerksamkeit.','https://www.mein-schoener-garten.de/pflanzen/haselnuss/haselnussstrauch-gewoehnliche-haselnuss');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Gemeiner Wacholder','Juniperus communis','Der Wacholder ist ein heimisches Wildgehölz und gehört zur Familie der Zypressengewächse. Es gibt von ihm zahlreiche Gartenformen – vom flach wachsenden Kriechwacholder bis zum schmal-aufrechten Säulen-Wacholder. Sie wachsen vorwiegend auf mageren, mäßig trockenen Böden – eine ökologische Nische, in der die meisten anderen heimischen Bäume und Sträucher nicht lebensfähig sind.','Zier- und Nutzpflanze','säulenförmig, aufrecht','April bis Juni',NULL,'ungiftig',NULL,'Ja','Fruchtschmuck, malerischer Wuchs','Gewürz','sonnig bis absonnig','sandig bis lehmig','trocken','neutral','arm','Wacholder kommt ohne besondere Pflegemaßnahmen aus.  Sollten die Pflanzen trotz ausreichend Sonnenlicht braune Triebspitzen zeigen, leiden sie in den meisten Fällen unter Magnesiummangel. Abhilfe schafft eine Gabe Bittersalz.','https://www.mein-schoener-garten.de/pflanzen/wacholder/gemeiner-wacholder');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Ananasminze','Mentha suaveolens ‘Variegata’','Die Ananasminze besticht nicht nur durch fruchtiges Aroma und wohltuende Frische, sondern auch durch ihre panaschierten Blätter. Sie zählt zu den Frucht-Minzen, die weniger Menthol enthalten als klassische Minzen und dafür mehr nach frischen Früchten duften und schmecken. Weil das fruchtige Aroma der Ananas überwiegt, ist die Ananasminze auch bei Kindern beliebt.','Nutzpflanze','aufrecht','Juli bis September','rosa, weiß','ungiftig','Ja',NULL,'Duftpflanze','Küchenkraut, Heilpflanze','sonnig bis halbschattig','sandig bis lehmig','frisch bis feucht','neutral','mäßig bis reich','Ananasminze ist anspruchslos und pflegeleicht. Die Erde sollte man gleichmäßig feucht halten und nicht austrocknen lassen. Vor allem Kübelpflanzen brauchen im Sommer regelmäßig Wasser und reagieren empfindlich auf Trockenheit. Gelegentliche Staunässe hingegen verzeihen sie.','https://www.mein-schoener-garten.de/pflanzen/minze-pfefferminze/ananasminze');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Bärlauch','Allium ursinum','Bärlauch ist eine beliebte ausdauernde und heilkräftige Würzpflanze. Am idealen Standort gedeiht das Wildgemüse nicht nur im Wald, sondern auch im Garten. Bärlauch gehört zur Familie der Lauchgewächse und ist mit Gemüse wie Knoblauch und Zwiebeln verwandt. Unverkennbar ist das am Duft, den die Pflanzen versprühen.','Nutzpflanze','überhängend, teppichbildend','April bis Juni','weiß','ungiftig',NULL,'Ja','Blütenschmuck, Duftpflanze','Küchenkraut, Heilpflanze','absonnig bis schattig','sandig bis lehmig','frisch bis feucht','alkalisch bis neutral','reich','Eine besondere Pflege des Bärlauchs ist nicht nötig, sofern er sich an seinem Standort wohlfühlt. Wichtig ist, dass der Boden während der Saison ausreichend feucht ist. Daher sollte man die Pflanzen in Trockenperioden auch etwas wässern.','https://www.mein-schoener-garten.de/pflanzen/lauch/baerlauch');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Echte Kamille','Matricaria chamomilla','Die Kamille wird seit Jahrhunderten für ihre beruhigende und schmerzlindernde Wirkung geschätzt. Im Spätmittelalter und in Kräuterbüchern von Klöstern wird stets die entzündungshemmende und heilende Wirkung der Pflanze beschrieben, die durchweg den Ruf als "gutes Kraut" genießt.','Zier- und Nutzpflanze','locker, buschig','Mai bis Juli','weiß, gelb','ungiftig',NULL,'Ja','Blütenschmuck','Heilpflanze','sonnig','sandig bis lehmig','trocken bis frisch','alkalisch bis neutral','reich','Einmal angewachsen ist die Kamille äußerst anspruchslos. Nach der Blüte können Sie die Pflanzen gegebenenfalls zurückschneiden, um neue Triebe zu fördern.','https://www.mein-schoener-garten.de/pflanzen/kamille/echte-kamille');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Koriander','Coriandrum sativum','Koriander mag man – oder man mag ihn nicht. Sein kräftiges Aroma wird vor allem in der asiatischen Küche geschätzt. Er gibt aber auch heimischen Gerichten die spezielle würzige Note. Ursprünglich stammt das Kraut aber aus dem Mittelmeerraum und dem Mittleren Osten, wo es schon vor 3.000 Jahren verwendet wurde.','Nutzpflanze','locker, buschig','Juni bis Juli','weiß','ungiftig','Ja',NULL,'Blattschmuck','Küchenkraut','sonnig bis schattig','sandig bis lehmig','trocken bis feucht','neutral','reich','In der Anzuchtphase ist regelmäßiges Gießen wichtig, um vorzeitiges Schossen zu verhindern. Ein aufgelockerter Boden fördert das Wachstum des Korianders.','https://www.mein-schoener-garten.de/pflanzen/koriander/koriander');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Petersilie','Petroselinum crispum','Petersilie zählt zusammen mit Schnittlauch zu den Küchenkräutern, die man unbedingt im Garten haben sollte. Mit der richtigen Fruchtfolge können fast das ganze Jahr hindurch feine aromatische Blätter geerntet werden. Petersilie enthält viele Mineralstoffe wie Eisen und Calcium, ätherische Öle sowie einen hohen Anteil an den Vitaminen A, B und C.','Nutzpflanze','aufrecht, dicht','Juni bis Juli','gelb','ungiftig',NULL,NULL,NULL,'Küchenkraut','sonnig bis halbschattig','sandig bis lehmig','frisch bis feucht','alkalisch bis neutral','mäßig','Petersilie braucht viel Feuchtigkeit, verträgt jedoch keine Staunässe. Das regelmäßige Lockern des Bodens mit der Hacke fördert einen erneuten Austrieb nach der ersten Ernte. Wie alle Doldenblütler darf Petersilie nur alle vier bis fünf Jahre auf demselben Beet angebaut werden. Auch andere Doldenblütler wie zum Beispiel Möhren sollten in dieser Zeit nicht auf dem Beet stehen.','https://www.mein-schoener-garten.de/pflanzen/petersilie/petersilie');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Schnittlauch','Allium schoenoprasum','Schnittlauch gehört neben der Petersilie zu den beliebtesten Küchenkräutern und schmeckt am besten frisch aus dem Garten – und mit seinen rosa Kugelblüten ist er auch ein toller Beetschmuck.','Nutzpflanze','aufrecht','Mai bis August','violett, rot','ungiftig','Ja','Ja','Blütenschmuck','Küchenkraut','sonnig','sandig bis lehmig','trocken bis feucht','alkalisch bis neutral','reich','Schnittlauch ist im Grunde sehr pflegeleicht. Er braucht allerdings im Sommer eine gute Wasserversorgung, sonst werden die Röhrenblätter trocken und strohig.','https://www.mein-schoener-garten.de/pflanzen/lauch/schnittlauch');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Avocadobaum','Persea americana','Der Avocadobaum ist robuster als man denkt und kann auch bei uns – im Kübel kultiviert – leckere Früchte tragen. Seit dem 18. Jahrhundert verbreitete sich der Avocadobaum weltweit in den Tropen und Subtropen und ist inzwischen eine der wirtschaftlich bedeutendsten Kulturpflanzen.','Zier- und Nutzpflanze','aufrecht','April bis Mai','grün, gelb',NULL,NULL,NULL,'Blütenschmuck, Blattschmuck','Obst','sonnig','sandig bis lehmig','frisch','neutral','reich','Je wärmer die Pflanze steht, umso mehr Wasser braucht sie. Allerdings verdunstet der Avocadobaum durch das ledrige Laub weniger Wasser als Pflanzen mit weichen Blättern. Gießen Sie also regelmäßig, aber mit Bedacht. Sowohl Staunässe oder als auch komplette Bodentrockenheit gilt es zu verhindern. Trockenschäden erkennt man an braunen Blatträndern.','https://www.mein-schoener-garten.de/pflanzen/avocado-isabellenholz/avocadobaum');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Lorbeer','Laurus nobilis','Der Lorbeer gilt schon seit alters her als Glücksbringer und Machtsymbol. Er bildet zusammen mit anderen immergrünen Gehölzen in Bergregionen niedrige, immergrüne Feuchtwälder, die fast ausschließlich vom häufigen Nebel mit Wasser versorgt werden.','Zier- und Nutzpflanze','kegelförmig, buschig','März bis Mai','weiß','ungiftig',NULL,NULL,'Blattschmuck, Duftpflanze','Heilpflanze, Gewürz','sonnig bis schattig','sandig bis lehmig','trocken bis feucht',NULL,'reich','Der Lorbeerbaum hat aufgrund seiner derben, vor Trockenheit geschützten Blätter keinen allzu hohen Wasserbedarf und verträgt Trockenheit ganz gut. Man gießt zwar so viel, dass die Erde komplett durchfeuchtet wird und sich das Wasser im Untersetzer sammelt, wartet dann aber ab, bis die Oberfläche des Wurzelballens trocken ist, bevor man die Pflanze erneut wässert.','https://www.mein-schoener-garten.de/pflanzen/lorbeer/lorbeer-lorbeerbaum');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Strauchmargarite','Argyranthemum frutescens','Mit charmanter Blütenfülle verbreiten die beliebten Strauchmargeriten eine fröhliche Atmosphäre auf Balkon und Terrasse. Sie zählt jedes Jahr zu den beliebtesten Blühpflanzen für Terrasse und Balkon.','Zierpflanze','kugelförmig, buschig','Mai bis Oktober','gelb, rosa, weiß','ungiftig',NULL,NULL,'Blütenschmuck',NULL,'sonnig','sandig bis lehmig','frisch bis feucht','neutral','mäßig','Grundsätzlich sind Strauchmargeriten äußerst anspruchslos in der Pflege. Abgeblühtes fällt aber leider nicht von alleine ab. Damit die Sommerblüher stets tipptopp aussehen und um Platz für neue Blütenknospen zu schaffen, ist regelmäßiges Ausputzen wichtig. Wichtig für gutes Durchblühen sind regelmäßige Wassergaben. An heißen Tagen am besten morgens und abends gießen und den Topf in','https://www.mein-schoener-garten.de/pflanzen/strauchmargerite/strauchmargerite');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Bischofskraut','Ammi visnaga','In Blumenbeeten wirkt das Bischofskraut als romantisches Beiwerk und auch seine winterstabilen Samenstände besitzen einen großen Zierwert. Im alten Ägypten schätzte man das Bischofskraut als Heilpflanze, heute werden die aus den Samen gewonnenen Wirkstoffe in der Homöopathie eingesetzt.','Zierpflanze','aufrecht, schirmförmig','Juni bis September','grün, weiß','ungiftig',NULL,NULL,'Blütenschmuck',NULL,'sonnig','lehmig','trocken bis frisch',NULL,NULL,'Das Bischofskraut kommt mit Böden jeder Art zurecht und verträgt auch längere Trockenphasen ganz gut. Damit sich die Pflanze durch Selbstaussaat im Garten hält, braucht sie offene Bodenstellen. Am richtigen Standort sind keinerlei Pflegemaßnahmen erforderlich.','https://www.mein-schoener-garten.de/pflanzen/bischofskraut/bischofskraut');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Cosmea','Cosmos bipinnatus','Cosmea gehören mit ihren filigranen Blüten zu den schönsten Sommerblühern. Ihre einfachen Schalenblüten sind von geradezu schlichter Schönheit und haben ihr den deutschen Beinamen Schmuckkörbchen eingebracht.','Zierpflanze','aufrecht','Juli bis Oktober','rot, rosa, weiß','ungiftig',NULL,NULL,'Blütenschmuck',NULL,'sonnig bis halbschattig','sandig bis lehmig','trocken bis frisch','neutral','reich','Kosmeen sind in der Pflege nicht sehr anspruchsvoll, normale Garten- oder Kübelpflanzenerde genügen ihnen vollauf. Einmal eingewachsen, kommen sie auch mit vorübergehender Trockenheit gut zurecht. Allerdings sollten Sie regelmäßig alle verwelkten Blüten herausschneiden, um eine fortwährende Nachblüte zu unterstützen.','https://www.mein-schoener-garten.de/pflanzen/schmuckkoerbchen/cosmea-schmuckkoerbchen');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Gewöhnliche Sonnenblume','Helianthus annuus','Sonnenblumen gehören zum Sommer wie die Sonne. Ihre riesigen gelben Blüten an den festen Stängeln strahlen über die anderen Pflanzen im Beet hinweg. Sie gehört zu den am schnellsten wachsenden Blumen im Ziergarten. Innerhalb weniger Wochen erreicht sie je nach Sorte eine stattliche Höhe von bis zu drei Metern.','Zierpflanze','aufrecht','Juli bis Oktober','gelb','ungiftig','Ja',NULL,'Blütenschmuck',NULL,'sonnig','sandig bis lehmig','trocken bis feucht',NULL,'reich','In der Wachstumsphase sollte die Gewöhnliche Sonnenblume regelmäßig gedüngt und gewässert werden. Sät man direkt ins Beet, müssen die jungen Keimlinge vor Schnecken geschützt werden.','https://www.mein-schoener-garten.de/pflanzen/sonnenblume/gewoehnliche-sonnenblume');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Klatschmohn','Papaver rhoeas','Als typische Ackerwildpflanze wächst Klatschmohn vor allem auf Äckern und Feldern mit Getreide, ist im Frühsommer auch an vielen Wegrändern anzutreffen. Klatschmohn setzt aber auch im Garten natürliche Farbakzente.','Zierpflanze','aufrecht','Mai bis Juli','rot',NULL,'Ja','Ja','Blütenschmuck',NULL,'sonnig','sandig','trocken',NULL,'mäßig','Wird Klatschmohn in den Garten ausgepflanzt, ist der Pflegeaufwand sehr gering. Wer die Wiederkehr im Folgejahr oder eine zu starke Ausbreitung vermeiden möchte, schneidet die Blüten ab, ehe sich Samen bilden. Im Topf wie im Beet empfiehlt es sich, Verblühtes regelmäßig zu entfernen. Das regt die Blütenbildung an und verlängert die Blütezeit. Bei Topfkultur wird täglich gegossen, im Garten muss man nur bei länger anhaltender Trockenheit aktiv werden.','https://www.mein-schoener-garten.de/pflanzen/mohn/klatschmohn');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Kornblume','Centaurea cyanus','Die Kornblume ist seit jeher das Sinnbild für natürliche zeitlose Schönheit. Die wilde Art prägt im Sommer ganze Landstriche mit ihrem unverwechselbaren Blau. Für naturnahe Gärten gibt es zudem neben blauen gefüllten Sorten auch welche in romantischen Farbtönen.','Zierpflanze','aufrecht, locker','Mai bis September','violett, blau','ungiftig','Ja','Ja','Blütenschmuck',NULL,'sonnig bis absonnig','sandig bis lehmig','trocken bis frisch','neutral','arm','Wer Verblühtes von Kornblumen regelmäßig abschneidet, hat lange Freude an den unkomplizierten Sommerblumen, denn sie treiben immer wieder neue Blüten aus.','https://www.mein-schoener-garten.de/pflanzen/flockenblume/kornblume');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Luzerne','Medicago sativa','Luzerne sind häufig in Mischungen für Blumenwiesen enthalten, die als Bienen- und Insektenweide angeboten werden. Außerdem ist sie eine ideale Gründüngungspflanze, die man zur Verbesserung oder Vorbereitung des Gemüsegartens einsetzt.','Zierpflanze','aufrecht','Juni bis September','violett, blau','ungiftig','Ja',NULL,'Blütenschmuck',NULL,'sonnig','sandig bis lehmig','trocken bis feucht','neutral','mäßig','Mit Ausnahme des regelmäßigen Wässerns bis zum Einwachsen der Jungpflanzen benötigt die Luzerne keine besondere Pflege. Möchte man Luzerne als Grünfutter verwenden, kann man sie ab 15 Zentimetern Wuchshöhe schneiden. Als Gründüngung mäht man die Luzerne vor der Samenreife ab und lässt das Schnittgut als Mulch auf der Fläche liegen.','https://www.mein-schoener-garten.de/pflanzen/luzerne-schneckenklee/luzerne');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Mittagsblume','Dorotheanthus bellidiformis','Mittagsblumen eignen sich als bodendeckende Randbepflanzungen für Beetflächen und als Einfassungen. Sie kommen auch in Schalen oder Balkonkästen gut zur Geltung – zum Beispiel in Kombination mit Hauswurz und anderen Sukkulenten.','Zierpflanze','flachwüchsig','Juni bis August','mehrfarbig',NULL,NULL,NULL,'Blütenschmuck',NULL,'sonnig','sandig bis lehmig','trocken','neutral','arm','Mittagsblumen müssen nur wenig gegossen werden, da die Blätter Wasser speichern können. Verblühte Stängel gilt es regelmäßig zu entfernen. Auf eine Düngung können und sollten Sie sogar verzichten. Zu üppig mit Nährstoffen versorgte Mittagsblumen werden blühfaul und krankheitsanfällig.','https://www.mein-schoener-garten.de/pflanzen/mittagsblumen/mittagsblume');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Ringelblume','Calendula officinalis','Ringelblumen sind klassische Bauerngartenpflanzen und werden gerne als Mischkulturpartner für Gemüse gepflanzt. Sie sondern mit ihren Wurzeln Giftstoffe ab, die Nematoden im Boden auf Abstand halten. Sie eignen sich aber auch sehr gut als Flächenpflanzung oder für gemischte Sommerblumenbeete und können verwendet werden, um Lücken im Staudenbeet zu schließen.','Zierpflanze','aufrecht, buschig','Mai bis Oktober','gelb, orange','ungiftig',NULL,NULL,'Blütenschmuck','Heilpflanze','sonnig bis absonnig','sandig bis lehmig','frisch','neutral','mäßig','Ringelblumen brauchen nur ein Minimum an Pflege. Eine Düngung mit rund zwei Liter reifem Kompost bei der Beetvorbereitung deckt die Nährstoffversorgung fürs ganze Jahr. Gegossen werden Ringelblumen nur mäßig, um den Blütenansatz zu fördern.','https://www.mein-schoener-garten.de/pflanzen/ringelblume/ringelblume');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Wilde Möhre','Daucus carota','Die Wilde Möhre eignet sich als hübscher Doldenblütler für naturnahe Bepflanzungen an sonnigen und mageren Standorten im Garten. Außerdem ist sie oft Bestandteil von Samenmischungen, die zum Anlegen von Wildblumenwiesen und einem Schmetterlingsgarten dienen.','Zier- und Nutzpflanze','aufrecht, schirmförmig','Juni bis Oktober','weiß',NULL,'Ja','Ja','Blütenschmuck','verzehrbar','sonnig','sandig','trocken',NULL,'arm','Halten Sie die Wilde Möhre bis zum Keimen ausreichend feucht und gießen Sie die Pflanze während langanhaltender Trockenperioden. Ansonsten sind keine weiteren Pflegemaßnahmen notwendig.','https://www.mein-schoener-garten.de/pflanzen/moehre/wilde-moehre');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Alpenaster','Aster alpinus','Die Alpenaster eignet sich sehr gut für Felsritzen im Steingarten, für Trockenmauern oder auch Terrassenbeete auf gut durchlässigen Böden. Schöne Begleiter sind andere Polsterstauden wie Seifenkraut, Pfingstnelke oder auch Gelber Lein.','Zierpflanze','aufrecht','Mai bis Juni','violett, rosa','ungiftig','Ja',NULL,'Blütenschmuck',NULL,'sonnig bis absonnig','sandig bis lehmig','trocken bis frisch','alkalisch','mäßig','Wie alle Astern sollte man auch die Alpenaster bei anhaltender Trockenheit wässern, auch wenn sie weitaus robuster und hitzeverträglicher ist als die meisten Herbstastern. Eine Düngung sollte – wenn überhaupt – nur sparsam erfolgen.','https://www.mein-schoener-garten.de/pflanzen/aster/alpenaster');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Bärwurz','Meum athamanticum','Die Bärwurz ist eng mit Liebstöckel und Petersilie verwandt. Vor allem aber ist das Kraut für seine Verwertung im Bayerischen Wald und im Erzgebirge bekannt, wo aus der Wurzel der Würzpflanze der verdauungsfördernde und magenstärkende Bärwurzschnaps gebrannt wird.','Zierpflanze','locker, buschig','Mai bis Juni','rosa, weiß',NULL,NULL,'Ja',NULL,'Heilpflanze','sonnig','sandig bis lehmig','frisch','sauer','mäßig','Bärwurz ist einfach zu kultivieren und benötigt für ein gutes Wachstum nur ausreichende, gleichmäßige Wassergaben. Will man Bärwurz in erster Linie als Wurzelgemüse ziehen, sollte man die Pflanze im Herbst mit gut verrottetem Dung mulchen.','https://www.mein-schoener-garten.de/pflanzen/baerwurz/baerwurz');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Blauer Eisenhut','Aconitum napellus','Wegen der Höhe und seines aufrechten Wuchses ragt der Blaue Eisenhut aus der Rabatte heraus. Er bildet einen Kontrast zu buschig wachsenden Pflanzen und lockert Beete auf. Besonders gut kommt er in gehölznahen Pflanzungen mit Astilbe und Silberkerze zur Geltung. Vermeiden Sie die Pflanze in Gärten, in denen Kinder spielen und sich Haustiere aufhalten.','Zierpflanze',NULL,'Juli bis August','violett, blau','stark giftig','Ja','Ja','Blütenschmuck',NULL,'halbschattig','sandig bis lehmig','frisch bis feucht','neutral','reich','Der Eisenhut freut sich über gelegentliche Gaben von Hornspänen als Düngung. Das Entfernen von verwelkten Blüten regt eine Nachblüte ab August an. Wenn die Blütenstände zu hoch werden und dazu neigen, auseinanderzufallen, sollten Sie sie mit einem Staudenring oder Stützstab stützen.','https://www.mein-schoener-garten.de/pflanzen/eisenhut/blauer-eisenhut');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Echte Katzenminze','Nepeta cataria','Da die Pflanzen dieser Gattung sehr robust und pflegeleicht sind, werden sie gerne als Alternative für Lavendel verwendet. Wer eine Katze besitzt und verhindern will, dass sie sich in der Pflanzung wälzt, sollte auf eine zitronenduftende Art wie die Echte Katzenminze zurückgreifen, da diese von den Katzen eher gemieden wird.','Zierpflanze','aufrecht, buschig','Juli bis August','violett, weiß','ungiftig','Ja','Ja','Blütenschmuck, Duftpflanze','Heilpflanze','sonnig','lehmig','trocken bis frisch','neutral','mäßig','Wenn Sie die Katzenminze nach der Hauptblüte bodennah zurückschneiden, wird die Pflanze dadurch zu einer zweiten Blüte im Spätsommer angeregt. Die Schnittmaßnahme verhindert zugleich, dass sich die Staude von selbst aussät – sofern dies nicht gewünscht ist.','https://www.mein-schoener-garten.de/pflanzen/katzenminze/echte-katzenminze');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Echter Lavendel','Lavandula angustifolia','Lavendel eignet sich gut als Duftstrauch in Trockenmauern oder Steingärten. Zudem können Sie mit dem Echten Lavendel wunderbar Beete einfassen und mediterran angelegte Gärten bereichern. Mit dem angenehmen Duft der Blüten, die überdies Bienen und andere Insekten anlocken, kommt er auch in Töpfen und Kübeln in der Nähe von Sitzplätzen zur Geltung.','Zier- und Nutzpflanze','aufrecht, buschig','Juli bis August','violett, blau','ungiftig','ja',NULL,'Blattschmuck, Duftpflanze','Küchenkraut, Heilpflanze','sonnig','sandig','trocken','neutral','arm','Der Duftstrauch ist äußerst anspruchslos in der Kultur. Halten Sie den Echten Lavendel möglichst mager. Lavendel gilt in milden Regionen als winterhart. In sehr rauen Lagen im Winter empfiehlt es sich, die Pflanzen mit etwas Reisig oder Mulch abzudecken.','https://www.mein-schoener-garten.de/pflanzen/lavendel/echter-lavendel');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Edelweiß','Leontopodium nivale','Die Besonderheit an der Edelweiß-Blüte ist ihr silbriger Glanz. Dieser entsteht durch die Reflexion vieler tausend winziger Luftbläschen in den wolligen Härchen auf den Hüllblättern. Der silbrig-weiße Schimmer lockt nektarsuchende Bestäuber-Insekten wie Fliegen und Käfer an.','Zierpflanze','rosettenbildend','Juni bis September','weiß','ungiftig','ja',NULL,'Blütenschmuck',NULL,'sonnig','sandig','frisch','neutral','arm','Nach der Pflanzung leicht angießen. Ansonsten wird nur bei starker Hitze und dann nur morgens oder abends gegossen. Staunässe im Beet muss ebenso wie lange Trockenperioden vermieden werden','https://www.mein-schoener-garten.de/pflanzen/edelweiss/edelweiss');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Himmelsleiter','Polemonium caeruleum','Die Himmelsleiter ist eine heimische Staude, die auch unter den Namen Blaue Jakobsleiter oder Blaues Sperrkraut bekannt ist. Die Blüten der nektar- und pollenreichen Himmelsleiter sind bei vielen Insekten wie Bienen, Hummeln und Schmetterlingen begehrt.','Zierpflanze','aufrecht','Juni bis Juli','blau',NULL,'Ja','Ja','Blütenschmuck',NULL,'absonnig bis halbschattig','sandig bis lehmig','frisch bis feucht','neutral','reich','Prinzipiell ist die Himmelsleiter robust und unkompliziert, was die Pflege anbelangt. Bei längeren Trockenphasen im Sommer muss die Staude allerdings intensiv gewässert werden. Wer nach der Blüte einen kompletten Rückschnitt der Jakobsleiter vornimmt, fördert einen zweiten Flor im September, der jedoch weniger üppig ausfällt als der erste.','https://www.mein-schoener-garten.de/pflanzen/jakobsleiter/jakobsleiter-himmelsleiter');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Löwenzahn','Taraxacum officinale','Der Gewöhnliche Löwenzahn, innerhalb der Familie der Korbblütler, ist eine mehrjährige Pflanze, die – wenn sie nicht gerade als Unkraut bekämpft wird – als wertvolle Heilpflanze zur Entgiftung geschätzt und auch als gesundes Essen in der Küche verwendet wird.','Zier- und Nutzpflanze','rosettenbildend','März bis Mai','gelb','ungiftig','Ja','Ja','Blütenschmuck','Küchenkraut, Heilpflanze','sonnig bis halbschattig','sandig bis tonig','trocken bis feucht','alkalisch bis neutral','reich','Einmal etabliert, benötigt der Löwenzahn keinerlei Pflege oder gärtnerische Zuwendung.','https://www.mein-schoener-garten.de/pflanzen/loewenzahn/loewenzahn');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Maiglöckchen','Convallaria majalis','Aus den angenehm süßlich riechenden Blüten – alle in die gleiche Richtung weisend – bilden sich ab Juli leuchtend rote Beeren, die jeweils zwei bis sechs Samen enthalten. Sie dienen Amseln und anderen Vögeln als nützliche Nahrungsquelle. Für den Menschen und einige Tiere wie Schweinen und Ziegen sind allerdings alle Pflanzenteile giftig.','Zierpflanze','flachwüchsig','Mai','weiß','stark giftig',NULL,'Ja','Blütenschmuck, Fruchtschmuck, malerischer Wuchs',NULL,'absonnig bis halbschattig','sandig bis lehmig','trocken bis frisch','alkalisch bis sauer','mäßig','Das Maiglöckchen gehört zu den weniger anspruchsvollen Pflanzen, nur starke Sonneneinstrahlung erträgt es nicht. Junge Pflanzen müssen ausreichend gewässert werden, um gut anzuwachsen. Nachdem das Maiglöckchen verblüht ist, werden die Blütenstände entfernt, die dunkelgrünen Blätter bleiben im Sommer stehen und haben mit ihrer kräftigen Farbe weiterhin eine schmückende Wirkung.','https://www.mein-schoener-garten.de/pflanzen/maigloeckchen/maigloeckchen');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Kugeldistel','Echinops ritro','Die Kugeldistel ist eine stattliche, äußerst dekorative Beetstaude, die auch in trockenen Lagen gut gedeiht. Sie ist aufgrund der geringen Wuchshöhe auch für kleinere Gärten geeignet. Die Blütenkugeln sind eine wertvolle Insektenweide und locken mit ihrem reichhaltigen Pollen- und Nektarangebot zahlreiche Gäste wie Schmetterlinge, Bienen und Hummeln in ihren Garten.','Zierpflanze','aufrecht','Juli bis September','blau','ungiftig','Ja','Ja','Blütenschmuck, Fruchtschmuck',NULL,'sonnig bis halbschattig','sandig bis lehmig','trocken bis feucht','neutral','mäßig','Die Kugeldistel ist eine äußerst pflegeleichte Staude, die auf nahezu jedem Boden gedeiht. Lediglich bei Kultivierung auf zu fetten oder feuchten Böden kann das Anbinden an Stützstäbe im Bedarfsfall eine sinnvolle Maßnahme sein, da die Pflanze unter diesen Bedingungen zum Kippen neigt','https://www.mein-schoener-garten.de/pflanzen/kugeldistel/ruthenische-kugeldistel');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Walderdbeeren','Fragaria vesca','Die Heimat der Walderdbeeren sind Waldränder und lichte Laub- und Nadelwälder, wo sie sich als Bodendecker ausbreitet und mit ihren nektarreichen Blüten und schmackhaften Früchten zahlreichen Tieren, wie Bienen, Fliegen und Nachtfalter, Nahrung bietet.','Nutzpflanze','überhängend','April bis Juni','weiß','ungiftig','ja','Ja','Blütenschmuck, Fruchtschmuck','Obst','sonnig bis halbschattig','lehmig','frisch bis feucht','alkalisch bis neutral','reich','Das Substrat der Walderdbeere sollte gleichmäßig feucht sein, das heißt, dass in heißen Sommern zusätzlich gegossen werden muss. Eine Mulchschicht aus Rinde oder Stroh mindert die Verdunstung und hält die tiefhängenden Früchte sauber.','https://www.mein-schoener-garten.de/pflanzen/erdbeere/walderdbeeren');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Waldmeister','Galium odoratum','Im Garten wird die halbhohe Waldmeister-Staude vorwiegend im Kräutergarten und zur Beetrandbepflanzung eingesetzt. Außerdem ist Waldmeister ein idealer Bodendecker für schattige, humusreiche Gartenbereiche unter Bäumen und Sträuchern. Bestäubt wird die Pflanze vor allem durch Bienen und einige Blatt- und Labkrautspanner-Arten.','Zier- und Nutzpflanze','flachwüchsig','April bis Mai','weiß','ungiftig',NULL,'Ja','Blütenschmuck, Rindenschmuck','Heilpflanze','halbschattig bis schattig','lehmig','frisch bis feucht','alkalisch','mäßig bis reich','An heißen Tagen sollte der Waldmeister regelmäßig gewässert werden. Geerntet wird Waldmeister direkt vor oder während der Blüte im Frühsommer. Dabei die Stängel über dem Boden abschneiden, die Pflanze kurz abbrausen, trockentupfen und entweder frisch oder getrocknet verwenden.','https://www.mein-schoener-garten.de/pflanzen/labkraut/waldmeister');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Adlerfarn','Pteridium aquilinum','Von den einen geliebt, von den anderen gefürchtet: Adlerfarn ist eine beeindruckende grüne Urweltpflanze. Einen großartigen Effekt entfaltet der Waldbewohner neben anderen Blattschmuckpflanzen wie zum Beispiel Funkien oder dem Schaublatt. Aber auch in der Kombination mit Blüten wirkt der Adlerfarn als grüner Strukturgeber.','Zierpflanze','aufrecht, überhängend',NULL,NULL,NULL,NULL,'Ja','Blattschmuck',NULL,'halbschattig bis schattig','sandig','frisch bis feucht','sauer','mäßg','In Trockenperioden sollten Sie den Adlerfarn etwas gießen. Im Frühjahr und Herbst lohnt sich eine Laubkompostgabe. Alte, welke Wedel können regelmäßig ausgeputzt werden. Achtung: Da der Adlerfarn giftig ist, sollten Sie bei der Pflanzung und Pflege Handschuhe tragen.','https://www.mein-schoener-garten.de/pflanzen/adlerfarn/adlerfarn');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Bärenfellgras','Festuca gautieri','Der Name des Grases ist Programm: Der beliebte Steingarten-Bewohner breitet sich langsam aber beständig durch kurze Rhizome aus. Mit der Zeit entsteht ein pelzartig, fein texturierter Gräserteppich mit einem Durchmesser von über einem Meter, der an ein grünes Bärenfell erinnert.','Zierpflanze','polsterbildend, dicht','Juni bis August','grün, gelb','ungiftig',NULL,NULL,'Blattschmuck',NULL,'absonnig bis halbschattig','sandig','trocken bis frisch','neutral','arm','Der Bärenfell-Schwingel ist robust und unempfindlich. Nur bei lang anhaltender Trockenheit sollten Sie das Gras wässern. Bärenfellgras muss nicht gedüngt werden – im Gegenteil: Eine Überversorgung mit Nährstoffen verkürzt die Lebensdauer der Gräser-Art.','https://www.mein-schoener-garten.de/pflanzen/schwingel/baerenfellgras-baerenfell-schwingel');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Büschelhaargras','Stipa capillata','Das Büschelhaargras ist ein filigranes einheimisches Ziergras, das dank der guten Strukturstabilität seiner Samenstände rund ums Jahr attraktiv ist. In Deutschland kommt es vor allem in warmen und trockenen Gebieten auf schütteren Trockenrasen vor.','Zierpflanze','aufrecht, überhängend','Juli bis August','weiß','ungiftig',NULL,'Ja','Blütenschmuck, Blattschmuck,  malerischer Wuchs',NULL,'sonnig','sandig','trocken','neutral','arm','Nährstoffreichere Böden fördern einen hohen und kräftigen Wuchs, doch muss man darauf achten, dass das lichtliebende Büschelhaargras nicht von konkurrenzstärkeren Arten bedrängt wird. Der Rückschnitt sollte erst im Frühjahr, rechtzeitig vor dem Neuaustrieb, erfolgen.','https://www.mein-schoener-garten.de/pflanzen/federgras/bueschelhaargras');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Goldrohrbambus','Phyllostachys aurea','Der Goldrohrbambus, auch Goldbambus genannt, ist eine zur Familie der Süßgräser gehörende Bambus-Art, die wegen ihrer hübschen gelben Halme bei uns sehr gerne verwendet wird. Die Halme stehen sehr dicht, was den immergrünen Goldrohrbambus zu einem hervorragenden Sichtschutz macht.','Zierpflanze','aufrecht, schirmförmig','Juli bis August','grün',NULL,NULL,NULL,'Blattschmuck, Rindenschmuck, malerischer Wuchs',NULL,'sonnig bis halbschattig','sandig bis lehmig','frisch bis feucht','neutral bis sauer','reich','Generell muss der Goldbambus nicht geschnitten werden, er ist aber wie die meisten Bambus-Arten sehr schnittverträglich. Möchten Sie Ihren Goldrohrbambus als Hecke verwenden, sollten Sie im Frühjahr oder Spätherbst zur Schere greifen, um ihn in die gewünschte Höhe zu bringen. Beachten Sie dabei, dass die Halme, sind sie einmal oben gestutzt, nicht weiterwachsen.','https://www.mein-schoener-garten.de/pflanzen/bambus-flachrohrbambus/goldrohrbambus-goldbambus');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Tomate','Solanum lycopersicum','Tomaten sind das beliebteste Gemüse für den eigenen Anbau. Tomaten sind einjährig und werden zwischen 20 Zentimeter (Buschtomaten) und zwei Meter (Stabtomaten) hoch. Ihre dunkelgrünen und fleischigen Blätter sind grob gefiedert und leicht behaart. Einzigartig ist der würzige Duft, den die Tomatenpflanzen verströmen.','Nutzpflanze','aufrecht, buschig',NULL,NULL,NULL,NULL,NULL,'Fruchtschmuck','Obst','sonnig','sandig bis lehmig',NULL,NULL,'reich','Wenn die Wurzeln gut angewachsen sind, können Sie die Tomatenpflanzen sparsamer wässern. Da die Pflanzen ein verzweigtes Wurzelsystem ausbilden, und sich auch gut mit Wasser versorgen können, sollten Sie dann gießen, wenn die Pflanzen morgens die Blätter hängen lassen. Um Pilzkrankheiten wie die Kraut- und Braunfäule zu verhindern, gilt es, die Tomatenpflanzen ausschließlich im Bodenbereich zu wässern und das Benetzen der Blätter zu vermeiden','https://www.mein-schoener-garten.de/pflanzen/gemuse/tomaten');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Zucchini','Cucurbita pepo var. giromontiina','Übersetzt heißt die Zucchini "kleiner Kürbis", was auf die enge Verwandtschaft mit dem richtigen Kürbis (italienisch: "zuccho") hinweist. Wobei: Klein bleiben die meisten Zucchini-Sorten nicht. Da sie rasch wachsen, müssen sich Gartenbesitzer sputen, um mit der Ernte und der Verwertung nachzukommen.','Nutzpflanze','dicht',NULL,'gelb',NULL,NULL,NULL,'Blütenschmuck, Blattschmuck, Fruchtschmuck','Gemüse','sonnig bis halbschattig','sandig',NULL,NULL,'reich','Zucchini sind Starkzehrer und benötigen daher eine gute Versorgung mit Nährstoffen. Bei der Beetvorbereitung sollten Sie drei bis fünf Liter reifen Kompost pro Quadratmeter flach in den Oberboden einharken. Eine wöchentliche Gabe Brennnesseljauche im Gießwasser (Mischungsverhältnis 1:10) stärkt die Blätter und sichert den Nährstoffnachschub.','https://www.mein-schoener-garten.de/pflanzen/gemuse/zucchini');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Endivie','Cichorium endivia','Die Endivie stammt aus der Familie der Korbblütler und ist ein hervorragender Salat für den Spätsommer und Herbst, wo sie noch einen guten Ertrag liefert. Da Endivien verhältnismäßig tief wurzeln, sollte der Boden tiefgründig und humusreich sein und nicht vernässen.','Nutzpflanze','rosettenbildend',NULL,NULL,NULL,NULL,NULL,NULL,'Gemüse','sonnig','sandig','trocken',NULL,NULL,'Die Aussaat mitten im Sommer erfordert vor allem in der Anfangsphase sowie in der Hauptwachstumszeit sorgfältiges Wässern. Eine gute Humusversorgung mit Komposterde und zusätzliches Mulchen reicht bei guten Böden als Düngung aus.','https://www.mein-schoener-garten.de/pflanzen/gemuse/endivie-friseesalat');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Peperoni','Capsicum annuum','Peperoni, auch Chili genannt, gehören zur Familie der Nachtschattengewächse . Eigentlich sind Peperoni botanisch gesehen das Gleiche wie die allseits beliebte Gemüsepaprika – nur eben je nach Sorte kleiner und deutlich schärfer! Die meisten im Handel erhältlichen Chili-Sorten sind Zuchtformen des Spanischen Pfeffers.','Nutzpflanze','buschig','Juni bis September','weiß','ungiftig',NULL,NULL,'Blütenschmuck, Fruchtschmuck','Gemüse','sonnig','sandig bis lehmig',NULL,'sauer',NULL,'Gießen Sie die Peperoni vor allem vor und während des Fruchtansatzes ausreichend und gleichmäßig. Wenn sie in dieser Phase zu wenig Wasser bekommt, kann es sein, dass die Peperoni die Blüten und Fruchtansätze abwirft. Nach dem Fruchtansatz können Sie dann durch zurückhaltendes Gießen bei scharfen Sorten den Capsaicin-Gehalt steigern.','https://www.mein-schoener-garten.de/pflanzen/gemuse/peperoni-chili');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Himbeere','Rubus idaeus','Himbeeren gehören zur großen Familie der Rosengewächse und werden wie Brombeeren, Heidelbeeren und Johannisbeeren gerne im Garten gepflanzt. Himbeersträucher werden je nach Sorte zwischen einem und zweieinhalb Meter hoch. Je nach Sorte bilden sich rundliche, kegelförmige oder langgestreckte Himbeeren aus. Sie sind leicht borstig behaart und oft bereift','Nutzpflanze','aufrecht, überhängend','Mai bis Juli','weiß','ungiftig',NULL,'Ja','Fruchtschmuck','Obst','sonnig','sandig bis lehmig','trocken bis frisch','neutral','reich','Himbeeren gehören zu den Flachwurzlern und reagieren auf Trockenheit und Staunässe sehr empfindlich. Das Pflanzbeet sollten Sie daher stets mulchen, zum Beispiel mit leicht angetrocknetem Rasenschnitt, Mulcherde oder Stroh','https://www.mein-schoener-garten.de/pflanzen/obst/himbeeren');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Zwetschgenbaum','Prunus domestica subsp. domestica','Die Zwetschge gehört botanisch zu den Pflaumen, zu denen auch Mirabellen zählen. Die Zwetschge benötigt zum Gedeihen einen geschützten, sonnigen sowie warmen Platz im Garten. Ideal ist zudem ein nährstoffreicher, humoser und feuchter Boden.','Nutzpflanze','aufrecht, schmal','April bis August','grün, weiß','ungiftig',NULL,NULL,'Fruchtschmuck','Obst','sonnig',NULL,'feucht',NULL,'reich','Es genügt, wenn Sie rund um den Zwetschgenbaum im Frühjahr hacken und im Mai eine Humusschicht auf der Baumscheibe verteilen. Im ersten Standjahr sollten Sie den Baum während längerer Trockenzeiten ausreichend wässern.','https://www.mein-schoener-garten.de/pflanzen/obst/zwetschge-zwetschgenbaum');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Apfelbaum','Malus domestica','Der Apfelbaum gehört zu den beliebtesten und weltweit am häufigsten angebauten Obstbäumen. Der Apfelbaum ist ein bis zu zehn Meter hoher Baum mit aufsteigenden oder abstehenden Ästen, tiefgehender Pfahlwurzel und schuppig unregelmäßig abblätternder Borke.','Nutzpflanze','aufrecht, abstehend','April bis Mai','rosa, weiß',NULL,NULL,NULL,'Fruchtschmuck','Obst','sonnig',NULL,'trocken bis frisch',NULL,'reich','Während Trockenperioden sollte der Apfelbaum von Zeit zu Zeit gewässert werden. Für ein gesundes Wachstum ist außerdem eine ausgewogene Nährstoffversorgung notwendig. Düngen Sie Ihren Apfelbaum das erste Mal im zeitigen Frühjahr (spätestens Mitte März), am besten mit einem Naturdünger wie Hornspäne.','https://www.mein-schoener-garten.de/pflanzen/obst/apfel-apfelbaum');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Kirschbaum','Prunus','Süßkirschen wachsen gewöhnlich als Baum mit großen Kronen und dicken Stämmen, Sauerkirschen eher als gedrungene Gehölze mit leicht überhängenden Zweigen in der Natur. Abhängig von der Veredelungsunterlage sowie der Erziehung sind im Gartenbereich auch Strauchformen, Spaliere, Säulenbäume sowie Bäume mit Kronenansatz in unterschiedlichster Höhe üblich.','Nutzpflanze','aufrecht, abstehend','April bis Mai','weiß',NULL,NULL,NULL,'Fruchtschmuck','Obst','sonnig bis halbschattig','lehmig bis tonig',NULL,NULL,'reich','Mit Nährstoffen versorgt man die Kirschbäume durch jährliches Mulchen im Frühjahr. Ein Weißanstrich verhindert im Winter Frostrisse.','https://www.mein-schoener-garten.de/pflanzen/obst/kirschbaum');
INSERT INTO plants(common_name,botanical_name,description_text,plant_category,growth_characteristics,blossoming_time,blossom_colour,poisonous,nectar_pollen,nativty,ornamental_value,utility_value,light,soil,soil_moisture,pH_value,nutrient_requirements,care_text,url) VALUES ('Strauchbasilikum','Ocimum basilicum','Bei uns gehört das Basilikum heute zum Standardsortiment jedes Kräuterregals, ist Bestandteil vieler leichter Gerichte und aromatisiert so manchen Sommerdrink. Neben dem Kräuterbeet im Garten lässt sich die Pflanze auch gut in einem Topf auf der hellen Fensterbank oder auf Balkon und Terrasse kultivieren.','Nutzpflanze','aufrecht, buschig','Juni bis September','weiß','ungiftig',NULL,NULL,NULL,'Küchenkraut, Heilpflanze','sonnig','sandig bis lehmig','feucht','neutral','reich','Achten Sie bitte darauf, dass die Erde des Strauchbasilikums niemals austrocknet. Vor allem in den Sommermonaten ist tägliches Gießen des Basilikums unerlässlich. Auch das Düngen ist ein wichtiger Punkt bei der Pflege: Regelmäßige Düngegaben mit speziellem Kräuterdünger halten sein Wachstum in Schwung. Und das Beste: Häufiges Ernten regt das Würzkraut zur Bildung immer neuer Blätter und Triebe an und fördert seinen buschigen Aufbau.','https://www.mein-schoener-garten.de/pflanzen/basilikum/strauchbasilikum');